sys_script_include.script:
var CIRDateUtils = function() {

	var validateDates = function (startDate, endDate, validatePast) {
		var status = !!startDate || !!endDate;		
		var nowGd = new GlideDateTime();
		var startDateGd;
		if (startDate) {
			startDateGd = new GlideDateTime();
			startDateGd.setDisplayValue(startDate);
			startDateGd.addDaysUTC(1);
			startDateGd.subtract(1000);
			if (validatePast) {
				status = status && startDateGd.onOrAfter(nowGd);
			}
		}
		var endDateGd;
		if (endDate) {
			endDateGd = new GlideDateTime();
			endDateGd.setDisplayValue(endDate);
			endDateGd.addDaysUTC(1);
			endDateGd.subtract(1000);
			if (validatePast) {
				status = status && endDateGd.onOrAfter(nowGd);
			}
		}

		if (endDateGd && startDateGd) {
			status = status && startDateGd.onOrBefore(endDateGd);
		}		
		return status;
	};

	return {
		ValidateDates: validateDates
	};
};
