sys_script_include.script:
var CIRKpiDbUtilsFP = function() {
    /**
     * Function helper to create the KPI Calculation record
     *
     * @param {Object} kpiObj - JSON object with all the related data necessary to create the record
     * @param {String} reportingDate - Reporting date for the current processed month
     * @return {String|null} - String with the record SysID in case of record created, null otherwise
     */
    var createKpiCalculation = function(kpiObj, reportingDate) {
        var grKPICalc = new GlideRecord(CIRConstants.Tables.KPI_CALCULATION);
        grKPICalc.initialize();
        grKPICalc.setValue('name', kpiObj.name);
        grKPICalc.setValue('reporting_date', reportingDate);
        grKPICalc.setValue('value', kpiObj.kpiValue);
        grKPICalc.setValue('period', kpiObj.periodSysId);
        grKPICalc.setValue('location', kpiObj.locationSysId);
        grKPICalc.setValue('calculation_type', kpiObj.calculationTypeSysId);

        return grKPICalc.insert();
    };

    /**
     * Function delete calculated KPIs from KPI Calculation table
     *
     * @param {String} locationSysId - Location SysID
     * @param {String} periodSysId - period SysID
     * @return {void}
     */
    var removeKpiCalculations = function(locationSysId, periodSysId) {
        var grKPICalc = new GlideRecord(CIRConstants.Tables.KPI_CALCULATION);
        grKPICalc.addQuery('location', locationSysId);
        grKPICalc.addQuery('period', periodSysId);
        grKPICalc.deleteMultiple();
    };

    return {
        CreateKpiCalculation: createKpiCalculation,
        RemoveKpiCalculations: removeKpiCalculations
    };
};
