sys_script_include.script:
var CIRKpiLocationFP = function() {
    /**
     * Function returning all the Locations including Global.
     *
     * @return {Array} - Array of locations
     */
    var getLocations = function() {
        var arrLocations = getLocationsWithWorkingHours();
        arrLocations.push({
            locationSysId: 'Global',
            locationName: 'Global'
        });
        return arrLocations;
    };

    /**
     * Function returning all the Locations with working hours
     *
     * @return {Array} - Array of locations
     */
    var getLocationsWithWorkingHours = function() {
        var arrLocations = [];
        var grWorkingHour = new GlideAggregate(CIRConstants.Tables.KPI_WORKING_HOURS);
        grWorkingHour.groupBy('location');
        grWorkingHour.query();
        while(grWorkingHour.next()) {
            arrLocations.push({
                locationSysId: grWorkingHour.getValue('location') + '',
                locationName: grWorkingHour.location.name + ''
            });
        }
        return arrLocations;
    };

    return {
        GetLocations: getLocations
    };
};
