sys_script_include.script:
var CIRGlobalHelperFP =  function() {
    var createUpdateRecord = function(createUpdateProps) {
        if(!gs.hasRole('admin') && !gs.hasRole('x_fru_cir.assessment_manager')) {
            return undefined;
        }
        var grCatalog = new GlideRecord(createUpdateProps.tableName);
        if(createUpdateProps.isCreate) {
            grCatalog.initialize();
        } else if(!grCatalog.get(createUpdateProps.updateRecordId)) {
            return undefined;
        }

        Object.keys(createUpdateProps.fieldValues).forEach(function(field) {
            grCatalog.setValue(field, createUpdateProps.fieldValues[field]);
        });

        return (createUpdateProps.isCreate ? grCatalog.insert() : grCatalog.update());
    };

    var modifyAssessment = function(attachmentId, sectionId) {
        var grSectionAttachment = new GlideRecord('sys_attachment');
        if(grSectionAttachment.get(attachmentId)) {
            grSectionAttachment.setValue('table_sys_id', sectionId);
            grSectionAttachment.update();
        }
    };

    var removeRecord = function(relatedData) {
        try {
            var grCurrent = new GlideRecord(relatedData.tableName);
            grCurrent.get(relatedData.sysId);
            return grCurrent.deleteRecord();
        } catch(err) {
            gs.info(err);
        }
    };

    return {
        CreateUpdateRecord: createUpdateRecord,
        ModifyAssessment: modifyAssessment,
        RemoveRecord: removeRecord
    };
};
