sys_script_include.script:
var CIRStaticProtectionClientUtils = Class.create();
CIRStaticProtectionClientUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getParentBuilding: function() {
		var spaceId = this.getParameter('sysparm_spaceId');

		var building = new CIRStaticProtectionUtils().GetSpaceParentBuilding(spaceId);
		return JSON.stringify(building);
	},

	// checks if every space in the list has his parent building on the building list. if not, erases it
	filterSpaces: function() {
		var spaces = this.getParameter('sysparm_spaceIds');
		var buildings = this.getParameter('sysparm_buildingIds');

		var filteredSpaces = new CIRStaticProtectionUtils().FltrSpacesFrmBuildings(buildings,spaces);
		return JSON.stringify(filteredSpaces);

	},

    type: 'CIRStaticProtectionClientUtils'
});
