sys_script_include.script:
var CIRGlobalScriptUtilsFP = function() {
    /**
     * function that will check if the Global Helper script is installed, enabled and up to date.
     * called in the Generate Category Objects UI Action
     * @return {Object} - JSON Object containing an error message and a boolean that states if the script is ok or not.
     */
    var checkGlobalScript = function() {
        var curVersion = gs.getProperty('x_fru_cir.global.template.current.version'),
            upgVersion = gs.getProperty('x_fru_cir.global.template.upgrade.version'),
            resp = {
                isOk: true,
            };

        if(!curVersion) {
            resp.msg = gs.getMessage('cir_assmt_global_script_install');
            resp.isOk = false;

        } else if(upgVersion != curVersion) {
            resp.msg = gs.getMessage('cir_assmt_global_script_upgrade');
            resp.isOk = false;

        } else {
            var grSI = new GlideRecord('sys_script_include');
            var isSI = grSI.get('name', 'CIRGlobalHelperFP');
            if(!isSI || (isSI && !grSI.active)) {
                resp.msg = gs.getMessage('cir_assmt_global_script_enable');
                resp.isOk = false;
            }
        }

        return resp;
    };

    /**
     * function that will update the property that stores the current version of the global script
     * called in the CIRGlobalScriptUtilsAjaxFP.updateGlobalScriptVersion function
     * @param {String} action - contains the action to perforom on the current version.
     */
    var updateGlobalScriptVersion = function(action) {
        var curVersion = gs.getProperty('x_fru_cir.global.template.current.version'),
            upgVersrion = gs.getProperty('x_fru_cir.global.template.upgrade.version');
        switch (action) {
            case 'upgrade':
                curVersion != upgVersrion ? gs.setProperty('x_fru_cir.global.template.current.version',
                    upgVersrion +
                    '') : '';
                break;

            case 'uninstall':
                gs.setProperty('x_fru_cir.global.template.current.version', '');
                break;
        }
    };

    return {
        CheckGlobalScript: checkGlobalScript,
        UpdateGlobalScriptVersion: updateGlobalScriptVersion
    };
};
