sys_script_include.script:
var CIRGlideUtilsFP = (function() {
    /**
     * Ensures only one record has been returned by query
     *
     * @param {String} gr - GlideRecord/GlideAggregate that has been queried (e.g. gr.query())
     * @throws Custom error if more than one record found. Logs warning if no records found
     */
    var validateSingleRowReturned = function(gr) {
        var EXPECTED_ROW_COUNT = 1;
        var GLIDE_RECORD_TYPE = '[object ScopedGlideRecord]';
        var GLIDE_AGGREGATE_TYPE = '[object ScopedGlideAggregate]';
        var ALLOWED_TYPES = [GLIDE_RECORD_TYPE, GLIDE_AGGREGATE_TYPE];

        var passedObjectType = gr.toString();
        if (ALLOWED_TYPES.indexOf(passedObjectType) < 0) {
            throw new Error('ValidateSingleRowReturned: Expected Glide Record, Actual type is ' + passedObjectType);
        }
        var rowCount = gr.getRowCount();
        if (rowCount > EXPECTED_ROW_COUNT) {
            throw new Error('Expected single row from query.  Actual count:  ' + rowCount);
        } else if (rowCount === 0) {
            gs.warn('Expected a single row but no rows returned.');
        }
    };
    return {
        ValidateSingleRowReturned: validateSingleRowReturned
    };
})();
