sys_security_acl.script:
if(current.control.risk && current.control.risk.investigation) {
	answer = new CIRRiskManagementUtils().CanApproverReadRecord(current.control.risk.investigation.sys_id + '');
} else if(current.control.risk && new CIRCaseManagementUtils().GetCasesRelatedToRisk(current.getUniqueValue(current.control.risk + '')).length > 0) {
	answer = new CIRCaseManagementUtils().CanApproverReadRiskRelatedCase(current.control.risk + '');
} else if(current.control.hazard) {
	answer = new CIRRiskManagementUtils().CanApproverReadRecordFromHazard(current.control.hazard.sys_id + '', current.control.risk.sys_id + '');
} else {
	answer = false;
}
