sys_security_acl.script:
if(current.risk && current.risk.investigation) {
	answer = new CIRRiskManagementUtils().CanApproverReadRecord(current.risk.investigation.sys_id + '');
} else if(current.risk && new CIRCaseManagementUtils().GetCasesRelatedToRisk(current.risk + '').length > 0) {
	answer = new CIRCaseManagementUtils().CanApproverReadRiskRelatedCase(current.risk + '');
} else if(current.hazard) {
	answer = new CIRRiskManagementUtils().CanApproverReadRecordFromHazard(current.hazard.sys_id + '');
} else {
	answer = false;
}
