sys_sg_data_item.query_condition_script:
(function getQueryString(input) {
    //Type appropriate comment here, and begin script below
   gs.info(input.cirInvestigationSysId + '>>>>>>>>input.cirInvestigationSysId');
    var flag = false;
    var queryString = '';
    var gr = new GlideRecord('x_fru_cir_m2m_contributing_factor');
    gr.addEncodedQuery('investigation=' + input.cirInvestigationSysId + '^contributing_factor.category=human_factors');
    gr.query();
    while (gr.next()) {
        flag = true;
        queryString += 'sys_id=' + gr.contributing_factor.sys_id + '^OR';
    }
    if (flag == false) queryString = 'sys_id=0';
    else {
        queryString = queryString.slice(0, -3);
        queryString += "^ORDERBYname";
    }
//    gs.info('FFFFFFFF' + queryString);
    return queryString;
})(input);
