sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
		var tables = x_fru_cir.CIRConstants.Tables;
	var grScenarioStep = new GlideRecord(tables.RESP_PLAN_EXEC_STEP);
	grScenarioStep.get(input.sys_id);

	var grScenarioExec = new GlideRecord(tables.RESP_PLAN_EXEC);
	grScenarioExec.get(grScenarioStep.response_plan);

	var grScenarioTemplate = new GlideRecord(tables.RESP_PLAN_TEMPLATE);
	grScenarioTemplate.addQuery('sys_id', grScenarioExec.response_plan_template.toString());
	grScenarioTemplate.query();

	if (grScenarioTemplate.next()) {
		gs.eventQueue('x_fru_cir.fsm.advance.to.next.step', grScenarioExec, grScenarioTemplate.managers.toString());
	}	
})(input);
