sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {

	var grAgentReport = new GlideRecord(CIRConstants.Tables.PROTECTION_AGENT_REPORT);
	grAgentReport.get(input.sys_id);

	var startTime =  input.start_time.split(' ')[1];
	var endTime = input.end_time.split(' ')[1];

	grAgentReport.start_time = startTime;
	grAgentReport.end_time = endTime;
	grAgentReport.guard_inspection = input.guard_inspection;
	grAgentReport.training_conducted = input.training_conducted;
	grAgentReport.service_updates = input.service_updates;
	grAgentReport.update();

})(input);
