sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
    var script = new x_fru_cir.CIRMobileUtils();
    if (script.VerifyExists(input.cirInvestigationSysId, input.cirContributingFactor)) {
        script.RemoveFromM2M(input.cirInvestigationSysId, input.cirContributingFactor);
        gs.addInfoMessage(gs.getMessage("Contributing factor has been removed from the investigation. Refresh to see the updated list"));
    } else {
        gs.addErrorMessage(gs.getMessage("The record does not exists"));
    }
})(input);
