sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
    //Type appropriate comment here, and begin script below

	var grStaticReports = new GlideRecord(CIRConstants.Tables.STATIC_SERVICE_REPORT);
	grStaticReports.addQuery('sys_id', input.sys_id);
	grStaticReports.query();

	if (grStaticReports.next()) {

		var actualFromTime =  input.actual_from_time.split(' ')[1];
		var actualToTime = input.actual_to_time.split(' ')[1];

		var grServiceOrders = new GlideRecord(CIRConstants.Tables.STATIC_SERVICE_ORDER);
		grServiceOrders.get(grStaticReports.service_order);

		grServiceOrders.actual_from_time = actualFromTime;
		grServiceOrders.actual_to_time = actualToTime;
		grServiceOrders.update();

		grStaticReports.from_time = actualFromTime;
		grStaticReports.to_time = actualToTime;
		grStaticReports.incidents = input.incidents;
		grStaticReports.access_control_issues = input.access_control_issues;
		grStaticReports.system_issues = input.system_issues;
		grStaticReports.state = '2';
		grStaticReports.update();
	}

})(input);
