sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {

	var tables = x_fru_cir.CIRConstants.Tables;
	var grSituation = new GlideRecord(tables.SITUATION);
	grSituation.get(input.sys_id);

	var grSituationEscalation = new GlideRecord(tables.SITUATION_ESCALATION_LEVEL);
	if(grSituation.getValue('escalation_level')) {
		grSituationEscalation.addQuery('order', '>' ,  grSituation.escalation_level.order );
	}
	grSituationEscalation.orderBy('order');
	grSituationEscalation.query();

	if (grSituationEscalation.next()) {
		grSituation.setValue('escalation_level', grSituationEscalation.getUniqueValue());
		grSituation.update();
		return;
	} else {
		gs.addErrorMessage(gs.getMessage('cir_sit_escalation_level'));
	}

})(input);
