sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
	gs.info('input.cirContributingFactor '+input.cirContributingFactor);
	gs.info('input.sys_id '+input.sys_id);
	var script = new x_fru_cir.CIRMobileUtils();
	if(script.VerifyExists(input.cirInvestigationSysId, input.sys_id)) {
		gs.addErrorMessage(gs.getMessage("Contributing factor has already been added to the investigation"));
	} else {
		script.AddInM2M(input.cirInvestigationSysId, input.sys_id);
		gs.addInfoMessage(gs.getMessage("Contributing factor has been added to the investigation"));
	}
})(input);
