sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {

	var tables = x_fru_cir.CIRConstants.Tables;
	var grSituationExecutions = new GlideRecord(tables.RESP_PLAN_EXEC);
	grSituationExecutions.addQuery('parent_situation', input.sys_id);
	grSituationExecutions.addQuery('state', '!=', 3 );
	grSituationExecutions.query();

		if (grSituationExecutions.next()) {
			gs.addErrorMessage(gs.getMessage('cir_sit_error_response_plan_open'));
			return;			
	}

	var grSituation = new GlideRecord(tables.SITUATION);
	grSituation.get(input.sys_id);
	grSituation.setValue('state', 1);
	grSituation.update();

})(input);
