sys_ui_page.client_script:
$j(document).ready(function() {
    $j('#fields')
        .find('option')
        .remove()
        .end()
        .append('<option value="none">${gs.getMessage("None")}</option>>')
        .val('none');
    var table = "";
    if (g_form.getValue('type') == "1") {
        table = ("x_fru_cir_internal_physical_security_incident");
    } else if (g_form.getValue('type') == "0") {
        table = ("x_fru_cir_external_physical_security_incident");
    }

    if (table != '') {
        var ga = new GlideAjax('CIRPSIAjaxUtils');
        ga.addParam('sysparm_name', 'getFields');
        ga.addParam('sysparm_table_name', table);
        ga.getXML(getIt);
    }
});
var type = 'email';
var newBody = "";
var newBodyEsp = "";

$j("#add_tag").click(function() {

    if ($j('#fields').val() != 'none') {
        if ($j('#field').val() == 'es') {
            if (type == 'email') {
                newBodyEsp = g_form.getValue('message_es') + ' #{' + $j('#fields').val() + '}';
                g_form.setValue('message_es', newBodyEsp);
            }
        } else if ($j('#field').val() == 'eng') {
            if (type == 'email') {
                newBody = g_form.getValue('message') + ' #{' + $j('#fields').val() + '}';
                g_form.setValue('message', newBody);

            }
        }

    }
    GlideModal.get().destroy();
});

function getIt(response) {
    var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
	var aaa = JSON.stringify(answer[1]);
	for (var i = 0; i < answer.length; i++) {
        $j('#fields').append($j('<option>', {
            value: answer[i].value
        }).text(getMessage(answer[i].label)));
    }
}
