sys_ui_page.client_script:
$j(document).ready(function (){
	var execMessageId = gel('execId').value;


	var ga = new GlideAjax('CIRSituationUtilsClient');
	ga.addParam('sysparm_name', 'getRecipList');
	ga.addParam('sysparm_execStep', execMessageId);

	ga.getXML(getAnswer);

	function getAnswer(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var userList = JSON.parse(answer);

		showList(userList);
		}

	function showList (userList){
		var body = '';
		var name = '';
		var email = '';
		var phone = '';

		if(userList.length > 0) {
			var header = "<tr>"
						+"<th> ${gs.getMessage('Name')}</th>"
						+"<th>${gs.getMessage('Email')}</th>"
						+"<th>${gs.getMessage('Phone Number')}</th>"
						+"</tr>";

			$j("#table_header").append(header);

			for(var i=0; i<userList.length;i ++) {
				name = userList[i].name ? userList[i].name : '-';
				email = userList[i].email ? userList[i].email : '-';
				phone = userList[i].phone ? userList[i].phone : '-';

				body+='<tr>'
					+ '<td>' + name + '</td>'
					+ '<td>' + email + '</td>'
					+ '<td>' + phone + '</td>'
					+ '</tr>';
			}
		} else {
			body += "<h3>${gs.getMessage('No contacts')}</h3>";
		}

		$j("#table_body").append(body);
	}			   
});
