sys_ui_page.client_script:
var buttonMsg = "";
if(document.getElementById('activate').value === 'false' && document.getElementById('published').value === 'true') {
	buttonMsg = "${JS:gs.getMessage('cir_assmt_unpublish')}";
} else if(document.getElementById('activate').value === 'false') {
	buttonMsg = "${JS:gs.getMessage('cir_assmt_deactivate')}";
} else if(document.getElementById('activate').value === 'true') {
	buttonMsg = "${JS:gs.getMessage('cir_assmt_activate')}";
}
document.getElementById('ok_button').className = "btn btn-destructive";
document.getElementById('ok_button').innerText = buttonMsg;
document.getElementById('cancel_button').className = "btn btn-default";

function cancelAction() {
    GlideDialogWindow.get().destroy();
    return false;
}
