sys_ui_page.client_script:
var cal = [];
var type = gel('type').value;

switch (type) {
	case 'ui_a': 
		getEntireSchedule();
		break;
	case 'bg':
		getEachBGSchedule();
		break;
}

//retrieve a calendar with all the Service Orders of all the bodyguards
function getEntireSchedule() {
	var ga = new GlideAjax('CIRPersonalProtectionClientUtils');
	ga.addParam('sysparm_name', 'getAllBGSchedule');
	ga.getXML(GetAnswer);
}

//retrieve a calendar with all the Service Orders for a given bodyguard
function getEachBGSchedule() {
	var bodyguardID = gel('bgSysId').value;

	var ga = new GlideAjax('CIRPersonalProtectionClientUtils');
	ga.addParam('sysparm_name', 'getBodyguardSO');
	ga.addParam('sysparm_bgSysID', bodyguardID);
	ga.getXML(GetAnswer);
}

function GetAnswer(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");
	cal = JSON.parse(answer);

	drawCalendar(cal);
}

//function used to create the calendar
//is here that the properties specific to the calendar API should be added or removed
function drawCalendar(serviceOrders) {
	var instanceLang = g_lang;
	var calendarEl = document.getElementById('calendar');

	var calendar = new FullCalendar.Calendar(calendarEl, {
		lang: instanceLang,
		locale: instanceLang,
		timeZone: 'local',

		plugins: ['dayGrid', 'timeGrid',  'list', 'interaction'],
		defaultView: 'dayGridMonth',

		allDaySlot: false,
		minTime: '00:00',
		maxTime: '24:00',

		weekNumbers: true,
		weekNumbersWithinDays: true,
		fixedWeekCount: false,
		navLinks: true,

		header: {
			left: 'prev,next',
			center: 'title',
			right: 'dayGridMonth,timeGridWeek,timeGridDay,list',
		},

		eventLimit: true,
		events: serviceOrders,
		displayEventTime: true,
		eventTimeFormat: {
			hour: 'numeric',
			minute: '2-digit',
			meridiem: 'short'
		},
	});

	calendar.render();
}
