sys_ui_policy.script_false:
function onCondition() {
	if (g_form.getValue('statue') === '1') {
		g_form.setReadOnly('service_type', false);
		g_form.setReadOnly('service', false);
		g_form.setReadOnly('bodyguards', false);
		g_form.setReadOnly('service_details.agenda', false);
		g_form.setReadOnly('service_details', false);
		g_form.setReadOnly('assigned_vehicle', false);
		g_form.setReadOnly('start_date', false);
		g_form.setReadOnly('end_date', false);
		g_form.setReadOnly('start_time', false);
		g_form.setReadOnly('end_time', false);
		g_form.setReadOnly('tools_used', false);
	}
}

sys_ui_policy.script_true:
function onCondition() {
	g_form.setReadOnly('service_type', true);
	g_form.setReadOnly('service', true);
	g_form.setReadOnly('bodyguards', true);
	g_form.setReadOnly('service_details', true);
	g_form.setReadOnly('service_details.agenda', true);
	g_form.setReadOnly('assigned_vehicle', true);
	g_form.setReadOnly('start_date', true);
	g_form.setReadOnly('end_date', true);
	g_form.setReadOnly('start_time', true);
	g_form.setReadOnly('end_time', true);
	g_form.setReadOnly('tools_used', true);
}
