sys_ui_policy.script_false:
function onCondition() {
	if (g_form.getValue('message_type') === '') {
        g_form.setVisible('message', true);
    }
    if (g_form.getValue('task_type') === 'action') {
        g_form.setVisible('message', false);
        g_form.setVisible('body', false);
    } else {
        g_form.setVisible('body', false);
        g_form.setVisible('message', true);
    }
}

sys_ui_policy.script_true:
function onCondition() {
	if (g_form.getValue('message_type') === '') {
        g_form.setVisible('message', true);
    } else if (g_form.getValue('message_type') === 'email') {
        g_form.setVisible('message', false);
        g_form.setVisible('body', true);
    }
    if (g_form.getValue('task_type') === 'action') {
        g_form.setVisible('body', false);
    }
}
