sys_widgets.script:
function sections() {
    return {
        'CIR Injuries/Illnesses': {
            'type': 'x_fru_cir_body_location_map'
        }
    };
}

function render() {
    var scope = gs.getCurrentScopeName();
    scope = (scope == "rhino.global" ? "" : scope + "_");
    var page = renderer.getPreference('type');
    return renderer.getRenderedPage(page);
}

function getEditLink() {
    var scope = gs.getCurrentScopeName();
    scope = (scope == "rhino.global" ? "" : scope + "_");
    var page = renderer.getPreference('type');
    return "sys_ui_page.do?sysparm_query=endpoint=" + page + '.do';
}
