sysauto_script.script:
var gdt = new GlideDateTime();
var grCrisisMember = new GlideRecord(CIRConstants.Tables.SITUATION_MEMBER);
grCrisisMember.addQuery('online', true);
grCrisisMember.query();

while(grCrisisMember.next()) {
	if(reachedMaxLoggedTime()) {
		grCrisisMember.setValue('online', false);
		grCrisisMember.setValue('last_access_time', gdt);
		grCrisisMember.update();
	}
}

function reachedMaxLoggedTime() {
	var lastAccess = new GlideDateTime(grCrisisMember.last_access_time);
	var dur = GlideDateTime.subtract(lastAccess, gdt).getNumericValue()/1000/60;

	if(dur >= 10) {
		return true;
	} else return false;
}
