sys_variable_value.value:
answer = checkBackup();

function checkBackup() {
	if (current.primary_backup == '') {		
		current.primary_backup = getBackupContact('primary');
		workflow.scratchpad.backup_contact = current.primary_backup;
		return 'no';
	} 
	
	if (current.secondary_backup == '') {
		current.secondary_backup = getBackupContact('secondary');
		workflow.scratchpad.backup_contact = current.secondary_backup;
		return 'no';
	} else {
		return 'yes';
	}
}

function getBackupContact(backupContact) {
	var userProfileGr = new GlideRecord(CIRConstants.Tables.USER_PROFILE);
	userProfileGr.addQuery('user', current.user);
	userProfileGr.query();

	if (userProfileGr.next()) {
		switch (backupContact) {
			case 'primary':
				return userProfileGr.primary_backup.toString();
			case 'secondary':
				return userProfileGr.secondary_backup.toString();
		}
	}
}

