workflow.scratchpad.message = getMessage();

function getMessage() {
	var stepMsgGr = new GlideRecord(CIRConstants.Tables.RESP_PLAN_STEP_MSG);
	stepMsgGr.addQuery('sys_id', current.step_message_execution.response_plan_step);
	stepMsgGr.query();

	if (stepMsgGr.next()) {
		return stepMsgGr.message.plain_text;
	}
	return '';
}

workflow.scratchpad.message = getMessage();

function getMessage() {
	var stepMsgGr = new GlideRecord(CIRConstants.Tables.RESP_PLAN_STEP_MSG);
	stepMsgGr.addQuery('sys_id', current.step_message_execution.response_plan_step);
	stepMsgGr.query();

	if (stepMsgGr.next()) {
		return stepMsgGr.message.plain_text;
	}
	return '';
}

var stepExecution = new GlideRecord(CIRConstants.Tables.RESP_PLAN_EXEC_STEP_MSG);
stepExecution.addQuery('sys_id', current.step_message_execution);
stepExecution.query();

if (stepExecution.next()) {
	if (checkIfBackupHasMsg(workflow.scratchpad.backup_contact)) {
		current.state = '3';
		current.feedback = workflow.scratchpad.feedback;
	} else {
		switch (current.message_type.toString()) {
			case 'email':
				gs.eventQueue('x_fru_cir.message.send', stepExecution, workflow.scratchpad.backup_contact);
				setBackupOnTeamMember(workflow.scratchpad.backup_contact);
				break;

			case 'sms':
				var fromNumber = gs.getProperty('x_fru_cir.twilio.phone.number').toString();
				var number = new CIRSituationUtils().GetPhoneNumbers(workflow.scratchpad.backup_contact);
				var notify = new sn_notify.NotifyScoped();

				notify.sendBulkSMS(fromNumber, number, workflow.scratchpad.message, stepExecution);
				setBackupOnTeamMember(workflow.scratchpad.backup_contact);
				break;

			case 'push':
				gs.eventQueue('x_fru_cir.send.push.message', stepExecution, workflow.scratchpad.backup_contact);
				setBackupOnTeamMember(workflow.scratchpad.backup_contact);
				break;

			case 'voice':
				var fromNumber = gs.getProperty('x_fru_cir.twilio.phone.number').toString();
				var number = new CIRSituationUtils().GetPhoneNumbers(workflow.scratchpad.backup_contact);
				var notify = new sn_notify.NotifyScoped();

				notify.call(fromNumber, number[0], null, null, null, stepExecution);
				setBackupOnTeamMember(workflow.scratchpad.backup_contact);
				break;		
		}

	}
}

function checkIfBackupHasMsg(backupId) {
	var msgRecipientGr = new GlideRecord(CIRConstants.Tables.MSG_EXEC_RECIPIENT);
	msgRecipientGr.addQuery('user', backupId);
	msgRecipientGr.addQuery('step_message_execution', current.step_message_execution);
	msgRecipientGr.addQuery('state', '3');
	msgRecipientGr.query();

	if (msgRecipientGr.next()) {
		workflow.scratchpad.feedback = msgRecipientGr.feedback;
		return true;
	}

	return false;
}

// gets the response team member and sets the new backup contact that was notified
function setBackupOnTeamMember(backupId) {	
	var grTMember = new GlideRecord(CIRConstants.Tables.RESPONSE_TEAM);
	grTMember.addQuery('response_execution_id', current.step_message_execution.response_plan.toString());
	grTMember.addQuery('user', current.user.toString());
	grTMember.query();
	
	if(grTMember.next()) {
		grTMember.setValue('backed_up_by', backupId);
		grTMember.update();
	}
}

