var answer = generateTextToSay();

/**
 * Generate the text to say and the language to say it in
 * 
 * @returns {{text: string, language: string}}
 */
function generateTextToSay() {
    // construct the result object
    var result = {
        // define the text to say
        text        : getCorrectResponse(),
        
        // and the language to say it in (see 
        // or query the notify_language table
        // for supported languages)
        language    : workflow.scratchpad.language + ""
    };
    
    // return the result
    return result;
}

function getCorrectResponse() {
	var input = parseInt(workflow.scratchpad.digits);
	var message = '';
	
	if(input == 1) {
		message =  workflow.scratchpad.negative_response;	
	} else {
		message =  workflow.scratchpad.positive_response;
	}
	
	workflow.scratchpad.input = input;
	return message.toString();
}

// generate the text to say and the language to say it in
var answer = generateTextToSay();

/**
