function getCorrectResponse() {
	var input = parseInt(workflow.scratchpad.digits);
	var message = '';
	
	if(input == 1) {
		message =  workflow.scratchpad.negative_response;	
	} else {
		message =  workflow.scratchpad.positive_response;
	}
	
	workflow.scratchpad.input = input;
	return message.toString();
}

var tableName = current.getValue('source_table');
var record = '';

if (tableName === CIRConstants.Tables.RESP_PLAN_EXEC_STEP_MSG || tableName === CIRConstants.Tables.RESP_PLAN_EXEC_STEP) {
	record = getRecord(CIRConstants.Tables.RESP_PLAN_STEP_MSG, current.source.response_plan_step);
	populateScratchpadFromStep(record);

} else {
	record = getRecord(tableName, current.source);
	workflow.scratchpad.primary_message = record.message ? record.message : record.message_content || '';
	workflow.scratchpad.language = getLanguage();//by default english. might change in the future
	workflow.scratchpad.feedback_required = false;
	workflow.scratchpad.negative_message = '';
	workflow.scratchpad.positive_message = '';
	workflow.scratchpad.negative_response = '';
	workflow.scratchpad.positive_response = '';
}

function populateScratchpadFromStep (stepRecord) {
	workflow.scratchpad.primary_message = stepRecord.message.plain_text;
	workflow.scratchpad.language =  stepRecord.message.language.language_code;//by default english. might change in the future
	workflow.scratchpad.feedback_required = stepRecord.feedback;
	workflow.scratchpad.negative_message = stepRecord.message.negative_message;
	workflow.scratchpad.positive_message = stepRecord.message.positive_message;
	workflow.scratchpad.negative_response = stepRecord.message.negative_response;
	workflow.scratchpad.positive_response = stepRecord.message.positive_response;
} 

function getRecord(table, sys_id) {
	var gr = new GlideRecord(table); 
	if(gr.get('sys_id', sys_id)) {
		return gr;
	}	
}

function getLanguage() {
	var language = gs.getSession().getLanguage();
	var code = '';
	
	switch (language) {
		case 'en':
			code = 'en-US';
			break;
			
		case 'es':
			code = 'es-ES';
			break;
		
		case 'pt':
			code = 'pt-BR';
			break;
		
		default: 
			code = 'en-US';
			break;	
	}
	
	return code;
}

