var phoneNumber = current.phone_number.toString();
var feedback = getFeedbackValue();

var grMsgRecip = new GlideRecord(CIRConstants.Tables.MSG_EXEC_RECIPIENT);
grMsgRecip.addQuery('step_message_execution', current.source +'');
grMsgRecip.addQuery('contact', current.phone_number.toString()).addOrCondition('primary_backup.mobile_phone', current.phone_number.toString()).addOrCondition('secondary_backup.mobile_phone', current.phone_number.toString());
grMsgRecip.query();

while(grMsgRecip.next()) {
	grMsgRecip.feedback = feedback ? feedback: '';
	grMsgRecip.state = feedback ? '3' : '4';
	grMsgRecip.update();
}

function getFeedbackValue() {
	var input = workflow.scratchpad.input;
	if(input) {
		if (input == 1) {
			return '1';
		} return '0';
	} else {
		return;
	}	
}

