function ifScript() {
	if (gs.getProperty('x_fru_cir.static_protection_enable_guard') === 'true') {
		return 'yes';
	}
	return 'no';
}

var hasReports = false;

var grReports = new GlideRecord(CIRConstants.Tables.STATIC_SERVICE_REPORT);
grReports.addQuery('service_order', current.sys_id.toString());
grReports.query();

if (grReports.hasNext()) {
	hasReports = true;
}

if (!hasReports) {
	var grStaticReport = new GlideRecord(CIRConstants.Tables.STATIC_SERVICE_REPORT);
	grStaticReport.initialize(); 
	grStaticReport.setValue('service_order', current.sys_id.toString());
	grStaticReport.insert();
}

