answer = checkReportStates(current.sys_id);

// get the reports states where the service order is the same as the current service order
function checkReportStates(serviceOrder){
	var reportsStates = [];
	var allCompleted = false;
	var grReport = new GlideRecord(CIRConstants.Tables.PROTECTION_AGENT_REPORT);
	grReport.addQuery('service_order', serviceOrder);
	grReport.query();

	while(grReport.next()){
		reportsStates.push(grReport.getValue('state'));
	}

	// if none of the agent report states are '0' or '1', it means that they are all '2', so all the reports have 'completed' as state
	if (reportsStates.indexOf('0') === -1 && reportsStates.indexOf('1') === -1 && reportsStates.indexOf('2') !== -1){
		allCompleted = true;
		current.setValue('state', 9);
		current.active = false;
	}
	return allCompleted;
}

